mapper.select:
select h.id,h.name,h.coordinate,h.location ,g.id as governancetypeid , g.`name` as governancetype ,h.thisstage,h.`completion`, h.plancompletiontime as plancompletiontime , h.plancompletiontime as strplancompletiontime, h.x,h.y,h.govertype,h.scale,h.scalegrad,h.district from hiddendanger h ,governancetype g where h.governancetype=g.id
AND h.district = #{district}
AND h.name like concat('%', #{name},'%')
and h.governancetype=#{governancetype}
limit #{rows} offset #{start}

SELECT count(*)  
from hiddendanger h ,governancetype g  
where h.governancetype=g.id 

	AND h.district = #{district}
	

	AND h.name like concat('%', #{name},'%')


	and h.governancetype=#{governancetype}

  		select id ,`name`, `location`,coordinate, `governancetype` as governancetypeid, `thisstage`, completion, `plancompletiontime` as strplancompletiontime
		from hiddendanger 
		where `id` = #{id}

mapper.update:
  		update hiddendanger set
				governancetype = #{governancetype}	
		 where id = #{id}
