mapper.select:
select r.`id`, r.`name`, h.`completion`,h.plancompletiontime as plancompletiontime ,h.plancompletiontime as strplancompletiontime,r.`progress`,r.`basicInfo`, r.`governanceInfo`, r.`remark`, r.`headcount`,r.`create_time`,r.hiddendanger_id,h.x,h.y,h.`name` as hiddendanger_name from relocationproject r,hiddendanger h where r.hiddendanger_id=h.id
AND r.name like concat('%', #{name},'%')
limit #{rows} offset #{start}
